import { useState } from "react";
import { useQuery } from "@tanstack/react-query";
import { Numeral } from "numeral";

import getPlayerStats from "./playerForm/getPlayerStats";

import PlayerForm from "./playerForm/PlayerForm";
const Player = () => {
  const [player, setPlayer] = useState("");
  const [moreStats, setMoreStats] = useState(false);
  const [hideStats, setHideStats] = useState(false);

  const playerHandler = (player) => {
    setPlayer(player);
  };

  const playerStatsResults = useQuery({
    queryKey: ["getPlayerStats", player],
    queryFn: getPlayerStats,
    enabled: Boolean(player),
  });

  const seasonAverages = playerStatsResults;

  // if (playerStatsResults.isSuccess) {
  //   console.log(player);
  //   if (playerStatsResults.data.error) {
  //     return (
  //       <>
  //         <PlayerForm
  //           playerHandler={playerHandler}
  //           setHideStats={setHideStats}
  //         />
  //         <div>{`${player.player.firstname} ${player.player.lastname} ${player.season} stats not found`}</div>
  //       </>
  //     );
  //   }
  // }

  return (
    <div>
      <PlayerForm playerHandler={playerHandler} setHideStats={setHideStats} />
      {seasonAverages.data && (
        <table>
          <tbody>
            <tr>
              <td>Minutes</td>
              <td>{seasonAverages.data.min ?? 0}</td>
            </tr>
            <tr>
              <td>Points</td>
              <td>{seasonAverages.data.pts ?? 0}</td>
            </tr>
            <tr>
              <td>Rebounds</td>
              <td>{seasonAverages.data.reb ?? 0}</td>
            </tr>
            <tr>
              <td>Assists</td>
              <td>{seasonAverages.data.ast ?? 0}</td>
            </tr>
            <tr>
              <td>Field Goal %</td>
              <td>
                {seasonAverages.data.fg_pct
                  ? Numeral(seasonAverages.data.fg_pct * 100).format("0.0")
                  : 0}
              </td>
            </tr>
            {moreStats && (
              <>
                <tr>
                  <td>Steals</td>
                  <td>{seasonAverages.data.stl ?? 0}</td>
                </tr>
                <tr>
                  <td>Blocks</td>
                  <td>{seasonAverages.data.blk ?? 0}</td>
                </tr>
                <tr>
                  <td>Turnovers</td>
                  <td>{seasonAverages.data.turnover ?? 0}</td>
                </tr>
                <tr>
                  <td>Field Goal Attempts</td>
                  <td>{seasonAverages.data.fga ?? 0}</td>
                </tr>
                <tr>
                  <td>3 Point %</td>
                  <td>
                    {seasonAverages.data.fg3_pct
                      ? seasonAverages.data.fg3_pct * 100
                      : 0}
                  </td>
                </tr>
                <tr>
                  <td>3 Point Attempts</td>
                  <td>{seasonAverages.data.fg3a ?? 0}</td>
                </tr>
              </>
            )}
          </tbody>
        </table>
      )}
      {hideStats && seasonAverages.data && (
        <button
          onClick={() => {
            setMoreStats(true);
            setHideStats(false);
          }}
        >
          More Stats
        </button>
      )}
      {moreStats && (
        <button
          onClick={() => {
            setMoreStats(false);
            setHideStats(true);
          }}
        >
          Close
        </button>
      )}
    </div>
  );
};
export default Player;
