import { useState } from "react";
import { useQuery } from "@tanstack/react-query";

import getPlayerStats from "./playerForm/getPlayerStats";

import PlayerForm from "./playerForm/PlayerForm";
const Player = () => {
  const [player, setPlayer] = useState("");
  const [moreStats, setMoreStats] = useState(false);
  const [hideStats, setHideStats] = useState(false);
  const [errorPlayer, setErrorPlayer] = useState("");

  const playerHandler = (player) => {
    setPlayer(player);
  };

  const playerStatsResults = useQuery({
    queryKey: ["getPlayerStats", player],
    queryFn: getPlayerStats,
    enabled: Boolean(player),
  });

  const seasonAverages = playerStatsResults;

  if (playerStatsResults.isSuccess) {
    if (playerStatsResults.data.error) {
      setErrorPlayer(`${player.firstnam} ${player.surname}`);
      return (
        <>
          <PlayerForm
            playerHandler={playerHandler}
            setHideStats={setHideStats}
            errorPlayer={errorPlayer}
          />
          <div>Player stats not found</div>
        </>
      );
    }
  }

  return (
    <div>
      <PlayerForm
        playerHandler={playerHandler}
        setHideStats={setHideStats}
        errorPlayer={errorPlayer}
      />
      {seasonAverages.data && (
        <table>
          <tbody>
            <tr>
              <td>Minutes</td>
              <td>{seasonAverages.data.min ?? 0}</td>
            </tr>
            <tr>
              <td>Points</td>
              <td>{seasonAverages.data.pts}</td>
            </tr>
            <tr>
              <td>Rebounds</td>
              <td>{seasonAverages.data.reb}</td>
            </tr>
            <tr>
              <td>Assists</td>
              <td>{seasonAverages.data.ast}</td>
            </tr>
            <tr>
              <td>Field Goal %</td>
              <td>{seasonAverages.data.fg_pct * 100}</td>
            </tr>
            {moreStats && (
              <>
                <tr>
                  <td>Steals</td>
                  <td>{seasonAverages.data.stl}</td>
                </tr>
                <tr>
                  <td>Blocks</td>
                  <td>{seasonAverages.data.blk}</td>
                </tr>
                <tr>
                  <td>Turnovers</td>
                  <td>{seasonAverages.data.turnover}</td>
                </tr>
                <tr>
                  <td>Field Goal Attempts</td>
                  <td>{seasonAverages.data.fga}</td>
                </tr>
                <tr>
                  <td>3 Point %</td>
                  <td>{seasonAverages.data.fg3_pct * 100}</td>
                </tr>
                <tr>
                  <td>3 Point Attempts</td>
                  <td>{seasonAverages.data.fg3a}</td>
                </tr>
              </>
            )}
          </tbody>
        </table>
      )}
      {hideStats && seasonAverages.data && (
        <button
          onClick={() => {
            setMoreStats(true);
            setHideStats(false);
          }}
        >
          More Stats
        </button>
      )}
      {moreStats && (
        <button
          onClick={() => {
            setMoreStats(false);
            setHideStats(true);
          }}
        >
          Close
        </button>
      )}
    </div>
  );
};
export default Player;
